n_runs = 1e2;count = 0;n = 1e5;%1D random walkerfor i = 1:n_runs   pos = 0;    for j = 1:n        u = rand();        if u < 0.5            pos = pos-1;        else            pos = pos+1;        end        if pos == 0            count = count+1;            break;        end    endendans = count/n_runs%2D random walkercount=0;for i = 1:n_runs    pos_hor = 0;    pos_ver = 0;    for j = 1:n        u = rand();        if u < 0.25            pos_hor = pos_hor - 1;        elseif u < 0.5            pos_hor = pos_hor + 1;        elseif u < 0.75            pos_ver = pos_ver - 1;        else            pos_ver = pos_ver + 1;        end        if (pos_hor == 0 && pos_ver == 0)            count = count + 1;            break;        end    endendans = count/n_runs%3D random walkercount = 0;k = 1/6;for i = 1:n_runs    pos_fb = 0;    pos_hor = 0;    pos_ver = 0;    for j = 1:n        u = rand();        if u < k            pos_hor = pos_hor - 1;        elseif u < 2*k            pos_hor = pos_hor + 1;        elseif u < 3*k            pos_ver = pos_ver - 1;        elseif u < 4*k            pos_ver = pos_ver + 1;        elseif u < 5*k            pos_fb = pos_fb - 1;        else            pos_fb = pos_fb + 1;        end        if (pos_hor == 0 && pos_ver == 0 && pos_fb == 0)            count = count + 1;            break;        end    endendans = count/n_runs%Probability of eventual return for 1D random walker = 1%Probability of eventual return for 2D random walker = 0.851000%Probability of eventual return for 3D random walker = 0.319033