n_runs = 1e5;count = 0;n = 100;r = 25;for i = 1:n_runs   pos = 0;    for j = 1:n        u = rand();        if u < 0.5            pos = pos - 1;        else            pos = pos + 1;        end        if pos >= r            count = count + 1;            break;        end    endendprac = count/n_runs%Practical answer = 0.011810%----------------------------------------------------------sum = 0;if mod((n+r),2) == 0    k = nchoosek(n,(n+r)/2);else    k = 0;endp = k/power(2,n);for i = r+1:n;    if mod((n+i),2) == 0        k = nchoosek(n,(n+i)/2);    else        k = 0;    end    sum = sum + k;endp = p + sum/power(2,n-1)%Theoretical answer = 0.012033