n = 10;fact = ones(n, 1);ster = ones(n, 1);x = 1:n;for i = 2:n  fact(i) = fact(i-1)*i;  ster(i) = (i/e)^i*sqrt(2*pi*i);endfactstererror = fact - sterratio = fact./sterplot(x, fact)hold on;plot(x, ster)xlabel('n')ylabel('n!')figure;plot(x, error)xlabel('n')ylabel('error')%fact =%%         1%         2%         6%        24%       120%       720%      5040%     40320%    362880%   3628800%%ster =%%   1.0000e+00%   1.9190e+00%   5.8362e+00%   2.3506e+01%   1.1802e+02%   7.1008e+02%   4.9804e+03%   3.9902e+04%   3.5954e+05%   3.5987e+06%%error =%%   0.0000e+00%   8.0996e-02%   1.6379e-01%   4.9382e-01%   1.9808e+00%   9.9218e+00%   5.9604e+01%   4.1760e+02%   3.3431e+03%   3.0104e+04%%ratio =%%   1.0000%   1.0422%   1.0281%   1.0210%   1.0168%   1.0140%   1.0120%   1.0105%   1.0093%   1.0084