%Group members:% Harshita Sahijwani : 201301179% Aarushi Sanghani: 201401403% Nirvisha Mankad: 201401421% Anishi Mehta: 201401439n_runs = 1e6;n = 5;r = 0.25;S = 1;K = 3;V = 0;V_avg = 0;u = 1.5;d = 0.8;for i = 1:n_runs    V = 0;    S = 1;    for j = 1:n        t = rand;        if t < 0.5            S = u*S;        else            S = d*S;        end    end    if S - K > 0        V = S - K;    end    V_avg = V_avg + V;endV_avg = V_avg/n_runs% V_avg =  0.27642%-----------------------------------------V = 0;S = 1;p = (1 + r - d)/(u - d);q = (u - 1 - r)/(u - d);for i = n:-1:1    if u^i*d^(n-i)*S < K        break;    end    V = V + nchoosek(n, i)*p^i*q^(n-i)*(u^i*d^(n-i)*S - K);endV = V/(1+r)^n% V =  0.27020