%Group members:%Group members:% Harshita Sahijwani : 201301179% Aarushi Sanghani: 201401403% Nirvisha Mankad: 201401421% Anishi Mehta: 201401439n_sim=1e5;n=100;pr=0;for j=1:n_sim  w=5;  b=7;  for i=1:n    p=w/(w+b);    r=rand;    if r<p      w=w+1;    else      b=b+1;    end  end  pr=pr+w/(w+b);endpr=pr/n_sim;disp('Proportion of white balls at 100')disp(pr)%Proportion of white balls at 100  %0.41747  %By Martingales, ratio of white balls = 5/(5+7) = 5/12 = 0.41667